mapper.select:
SELECT principle, city.note AS province, count(*) AS number FROM machine_info m LEFT JOIN careacodetblqg city ON city. CODE = m.province
and install_time >= #{startTime}
and install_time <= #{endTime}
GROUP BY principle, province ORDER BY number DESC

SELECT principle, count(*) AS number FROM machine_info
and install_time >= #{startTime}
and install_time <= #{endTime}
GROUP BY principle ORDER BY number DESC
